test_that("test_mappedRTfromAlignObj", {
  AlignObj <- testAlignObj()
  data(XIC_KLYAGAILEV_2_DIAlignR, package="DIAlignR")
  XICs <- XIC_KLYAGAILEV_2_DIAlignR
  tVec.ref <-  XICs[["run2"]][["KLYAGAILEV_2"]][[1]][, "time"]
  tVec.eXp <-  XICs[["run0"]][["KLYAGAILEV_2"]][[1]][, "time"]
  expect_equal(mappedRTfromAlignObj(refRT= 4449.35, tVec.ref, tVec.eXp, AlignObj), 4388.1)
})
